//Convencion Capital caracter, and
//camelCase
//Un JSk snipet
//Tell that Css will've injected

/*Passing Data via  "Props" Key concepts
   pass data to the custome component 
*/
/* 
  Multiple componets
*/
import React, { useState } from "react";// changes to the values should reflects 
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";
import "./ExpenseItem.css";

const ExpenseItem = (props) => {
  useState();//React hook to check for changes
  let title = props.title;
  const clickHandler = () => {
    title = "Updated";
  };

  return (
    <Card className="expense-item">
      <ExpenseDate date={props.date} />
      <div className="expense-item__description ">
        <h2>{title}</h2>
        <div className="expense-item__price">{props.amount}</div>
      </div>
      <button onClick={clickHandler}>Change Title</button>
    </Card>
  );
};

export default ExpenseItem;
